import React, { useState } from 'react'

function Hook() {
    const [estado, setEstado] = useState(0)

    return (
        <>
            <h1>{estado}</h1>
            <button onClick={() => setEstado(estado + 1)}>Alternar</button>
        </>
    )
}

export default Hook
