import React, { useState } from 'react'

function Hook() {
    const [estado, setEstado] = useState({
        nome: '',
        sobrenome: ''
    })


    function handleInput(e) {
        setEstado({ [e.target.name]: e.target.value })
        console.log(e.target.value)
    }



    /*function increment() {
        if (estado < 10) {
            setEstado(estado + 1)
        }
    }

    function decrement() {
        if (estado > 0) {
            setEstado(estado - 1)
        }
    }*/

    return (
        <form>
            <div>
                <input
                    name="nome"
                    value={estado.nome}
                    placeholder="Entre com seu nome"
                    onChange={this.handleInput}
                />
            </div>
            <div>
                <input
                    name="sobrenome"
                    value={estado.sobrenome}
                    placeholder="Entre com seu sobrenome"
                    onChange={this.handleInput}
                />
            </div>


            <button type="submit">Enviar</button>
        </form>
    )
}

export default Hook
