import React, { useState } from 'react'

function Hook() {
    const [estado, setEstado] = useState(0)

    function increment() {
        if({estado} < 10) {
            setEstado(estado + 1)
        }
    }

    return (
        <>
            <button onClick={ increment() }>Increment</button>
            <h1>{estado}</h1>
            <button onClick={() => setEstado(estado - 1)}>Decrement</button>
        </>
    )
}

export default Hook
