import React, { useState } from 'react'

function Hook() {
    const [estado, setEstado] = useState({
        nome: '',
        sobrenome: ''
    })


    handleInput = (e) => {
        setEstado({[e.target.name]: e.target.value})
    }

    

    /*function increment() {
        if (estado < 10) {
            setEstado(estado + 1)
        }
    }

    function decrement() {
        if (estado > 0) {
            setEstado(estado - 1)
        }
    }*/

    return (
        <form>
            <input
                name="nome"
                value={estado.nome}
                placeholder="Entre com seu nome"
                onChange={ handleInput }
            />


            <button onClick={decrement}>Decrement</button>
        </form>
    )
}

export default Hook
